var _ = require('lodash');
// NOTE: only 50 vertical spaces to work with
module.exports = Texts;
function Texts(interval, wordWrapWidth, maxY, startY, formula) {
	var self = this;
	this.texts = _getDefaultTexts();
	// generate all texts
	var allText = [];
	_.each(this.texts, function (entry,key){
		console.log('!!! key: ' + key);
		_.each(entry.text, function (chr) {
			allText.push(chr);
		});
	});


	this.texts.allTexts = {
		info: 'all texts combined',
		startX: 0,
		startY: startY,
		width: wordWrapWidth,
		height: Math.floor(allText.length/wordWrapWidth),
		text: allText
	};

	this.maxY = maxY;

	var _formula = function () {
		formula(self.texts.allTexts);
	};
	if (interval >= 0) setInterval(_formula, interval);
}
_.extend(Texts.prototype, {
	getAnyText: function (x, y) {
		return this.getText('allTexts', x, y);
	},
	getText: function (name, x, y) {
		if (x < this.texts[name].startX) return undefined;
		if ((x - this.texts[name].startX) >= this.texts[name].width) return undefined;

		//var overFlow = this.texts[name].startY + this.texts[name].height - this.maxY;
		//if (overFlow > 0 && y < overFlow) {
		//	y = this.maxHeight + y;
		//} else {
			if (y < this.texts[name].startY) return undefined;
		//}
		y = y - this.texts[name].startY;

		var i = (y*this.texts[name].width + x) - this.texts[name].startX;
		return this.texts[name].text[i];
	}
});

